import React, {Component} from 'react';
import { auth, createUserDocument } from './firebase';
import Login from './Signin';
import Home2 from './Home2';
import shoenw from './img/shoenw.png'


class App extends Component {

  constructor(){
    super();
    this.state = {
      user: null
    }
  }

  componentDidMount(){
    this.authListener();
  }

  authListener(){
    auth.onAuthStateChanged((user) => {
      if(user){
        this.setState({user});
      }else{
        this.setState({user:null});
      }
    });
  }

  render(){
    return (
      <>
      <div className="navbar">
                <img src={shoenw} className="logo" />
                <ul>
                    <li className=''>
                        <Link to='/' className=''> Home </Link>
                    </li>
                    <li className=''>
                        <Link to='/about-us' className=''> About Us </Link>
                    </li>
                    <li className=''>
                        <Link to='/contact-us' className=''> Contact Us</Link>
                    </li>
                    <li className=''>
                        <Link to='/Signin' className=''>Sign Up/Sign In </Link>
                    </li>
                </ul> 
            </div>
        <div>
          {this.state.user ? (<Home2 />) : (<Login />)}
        </div>
      </>
    );
  }
}

export default App;




















