import React, { Suspense, useEffect, useRef, useState } from 'react'
import {Link} from 'react-router-dom';
import shoenw from '../img/shoenw.png';
import './Payment.css';
import firebaseConfig from "../firebase";

const Payment = () => {
  const [size, setSize] = useState("");
  const [qty, setQty] = useState("");
  const [message, setMessage] = useState("");

  const [loader, setLoader] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoader(true);

    firebaseConfig.firestore().collection("Shipping Address")
      .add({
        Size: size,
        Quantity: qty,
        message: message,
      })
      .then(() => {
        setLoader(false);
        alert("Your Address has been submittedðŸ‘");
      })
      .catch((error) => {
        alert(error.message);
        setLoader(false);
      });

    setSize("");
    setQty("");
    setMessage("");
  };

  return (
    <>
    <div className="navbar">
                <img src={shoenw} className="logo" />

    </div>

    <form className="paymentform" onSubmit={handleSubmit}>

      <h1 className="Checkout">Check Out</h1>

      <input type="radio"
        placeholder="Quantity"
        value={qty}
        onChange={(e) => setEmail(e.target.value)}
      />

      <input type="number"
        placeholder="size"
        value={size}
        onChange={(e) => setName(e.target.value)}
      />

      <input type="number"
        placeholder="Quantity"
        value={qty}
        onChange={(e) => setEmail(e.target.value)}
      />


      <textarea
        placeholder="Message"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
      ></textarea>

      <button
        type="submit"
        style={{ background: loader ? "#ccc" : " #19ffa7" }}
      >
        Send
      </button>

    </form>

    </>
  );
};


export default Payment;
