import React, {useState} from 'react';
import {Link} from 'react-router-dom';
import './Navbar.css';

function Navbar() {
    const[click, setClick] = useState(false);

    const handleClick = () => setClick(!click); {/* opposite of click setstate */}
    const closeMobileMenu = () => setClick(false);
    return (
        <>
            <nav className="navbar">
                <div className="navbar-container">
                    <Link to="/" className="navbar-logo"> {/* Link replaces the a tag in React */}
                      SHOENIQUE <i className="fab fa-typo3"></i> {/* icon from fontawesome */}
                    </Link>

                    {/* burger icon from fontawesome */}
                    <div className="menu-icon" onClick={handleClick}> {/* use onClick event to make the icon changes */}
                        <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
                        {/* click state, if click the icon, will change to 'fas fa-times' class. */}
                        {/* click again, change to 'fas fa-bars' class */}
                    </div>

                    <ul className={click ? 'nav-menu active' : 'nav-menu'}> {/* this for navbar on mobile UI */}
                        <li className='nav-item'>
                            <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                                Home
                            </Link>
                        </li>
                        <li className='nav-item'>
                            <Link to='/services' className='nav-links' onClick={closeMobileMenu}>
                                Services
                            </Link>
                        </li>
                        <li className='nav-item'>
                            <Link to='/products' className='nav-links' onClick={closeMobileMenu}>
                                Products
                            </Link>
                        </li>
                        <li className='nav-item'>
                            <Link to='/sign-up' className='nav-links-mobile' onClick={closeMobileMenu}>
                                Sign Up
                            </Link>
                        </li>
                    </ul>

                </div>
            </nav>

        </>
    )
}

export default Navbar
