import React, { Suspense, useEffect, useRef, useState } from 'react'
import {Link} from 'react-router-dom';
import shoenw from '../img/shoenw.png';
import './Payment.css';
import firebaseConfig from "../firebase";

const Payment = () => {
  const [mwsize, setMWsize] = useState("");
  const [size, setSize] = useState("");
  const [qty, setQty] = useState("");
  const [fullname, setFname] = useState("");
  const [email, setEmail] = useState("");
  const [address, setAddress] = useState("");
  const [province, setProvince] = useState("");
  const [zip, setZip] = useState("");

  const [loader, setLoader] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoader(true);

    firebaseConfig.firestore().collection("Shipping Address")
      .add({
        MorWsize: mwsize,
        Size: size,
        Quantity: qty,
        Fullname: fullname,
        Email: email,
        Address: address,
        Province: province,
        zip: zip
      })
      .then(() => {
        setLoader(false);
        alert("Your Address has been submitted👍");
      })
      .catch((error) => {
        alert(error.message);
        setLoader(false);
      });

    setMWsize("");  
    setSize("");
    setQty("");
    setFname("");
    setEmail("");
    setAddress("");
    setProvince("");
    setZip("");
  };

  return (
    <>
    <div className="navbar">
                <img src={shoenw} className="logo" />

    </div>
    <div class="container">
  <h1>Shipping</h1>
  <p>Please enter your shipping details.</p>
  <hr />
  <div class="form">

  <div class="fields fields--2">
    <label class="field">
      <span class="field__label" for="firstname">First name</span>
      <input class="field__input" type="text" id="firstname" value="John" />
    </label>
    <label class="field">
      <span class="field__label" for="lastname">Last name</span>
      <input class="field__input" type="text" id="lastname" value="Doe" />
    </label>
  </div>
  <label class="field">
    <span class="field__label" for="address">Address</span>
    <input class="field__input" type="text" id="address" />
  </label>
  <label class="field">
    <span class="field__label" for="country">Country</span>
    <select class="field__input" id="country">
      <option value=""></option>
      <option value="unitedstates">United States</option>
    </select>
  </label>
  <div class="fields fields--3">
    <label class="field">
      <span class="field__label" for="zipcode">Zip code</span>
      <input class="field__input" type="text" id="zipcode" />
    </label>
    <label class="field">
      <span class="field__label" for="city">City</span>
      <input class="field__input" type="text" id="city" />
    </label>
    <label class="field">
      <span class="field__label" for="state">State</span>
      <select class="field__input" id="state">
        <option value=""></option>
      </select>
    </label>
    </div>
    </div>
    <hr></hr>
    <button class="button">Continue</button>
    </div>

    </>
  );
};


export default Payment;
